'use strict';

jQuery.open = function () {

	var href = arguments[0];
	var target = arguments[1];
	var type_href = ({}).toString.call(href).match(/\s([a-z|A-Z]+)/)[1].toLowerCase();
	var type_target = ({}).toString.call(target).match(/\s([a-z|A-Z]+)/)[1].toLowerCase();

	href = (href) ? ((type_href == 'string') ? 'href="' + href + '"' : 'href="#"') : 'href="#"';
	target = (target) ? ((type_target == 'boolean') ? 'target="_blank"' : '') : '';

	$('body').append('<a ' + href + ' id="open" ' + target + ' style="display: none;"><span></span></a>');
	$('#open').find('span').trigger('click');

};